def count_complete_binary_trees(n):
    def count_rec(k):
        if k == 0:
            return 1
        if k == 1:
            return 0  # 0個の頂点を持つツリーは1つ
        return count_rec(k - 1) + count_rec(k - 2)  # それぞれ左の子と右の子を持つ

